import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: MyImageViewer(),
    );
  }
}

class MyImageViewer extends StatefulWidget {
  @override
  _MyImageViewerState createState() => _MyImageViewerState();
}

class _MyImageViewerState extends State<MyImageViewer> {
  double _imageScale = 1.0;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Image Viewer'),
      ),
      body: GestureDetector(
        onScaleUpdate: (ScaleUpdateDetails details) {

          setState(() {
            _imageScale = details.scale;
          });
        },
        onHorizontalDragEnd: (DragEndDetails details) {

          print('Swiped ${details.primaryVelocity}');

        },
        child: Center(
          child: Transform.scale(
            scale: _imageScale,
            child: Image.network(
              'data:image/png;base64,',
              width: 300.0,
              height: 300.0,
              fit: BoxFit.contain,
            ),
          ),
        ),
      ),
    );
  }
}